import React, { Component } from "react";

export class Search extends Component {
  state = {
    from: "",
    to: ""
  };
  //   input
  handleInput = event => {
    event.preventDefault();

    this.setState({
      [event.target.name]: event.target.value
    });
  };
  //   submit
  handleSubmit = event => {
    event.preventDefault();
    const searchArray = this.props.search.filter(
      flight =>
        flight.from === event.target.value && flight.to === event.target.value
    );
    this.setState({
      search: searchArray
    });
    console.log(this.state);
  };
  render() {
    return (
      <div>
        <form className="form-group" onSubmit={this.handleSubmit}>
          <label htmlFor="from">From</label>
          <input
            type="text"
            name="from"
            value={this.state.from}
            onChange={this.handleInput}
          />
          <label htmlFor="to">To</label>
          <input
            type="text"
            name="to"
            value={this.state.to}
            onChange={this.handleInput}
          />
          <button type="button">Search</button>
        </form>
        <table>
          {this.props.search.map(flight => (
            <tr>
              <th>flight.from</th>
              <th>flight.to</th>
            </tr>
          ))}
        </table>
      </div>
    );
  }
}

export default Search;
