import React, { Component } from "react";
import shortid from "shortid";
export class Search extends Component {
  state = {
    from: "",
    to: "",
    id: "",
    result: []
  };
  //   input
  handleInput = event => {
    event.preventDefault();

    this.setState({
      id: shortid.generate(),
      [event.target.name]: event.target.value
    });
  };
  //   submit
  handleSubmit = event => {
    event.preventDefault();

    const result = this.props.searchObj.filter(
      flight => flight.from === this.state.from && flight.to === this.state.to
    );
    this.setState({
      result: result
    });
    this.forceUpdate();
    console.log(result);
  };
  render() {
    return (
      <div>
        <form className="form-group row" onSubmit={this.handleSubmit}>
          <label htmlFor="from">From</label>
          <input
            type="text"
            name="from"
            value={this.state.from}
            onChange={this.handleInput}
            className="form-control"
          />
          <label htmlFor="to">To</label>
          <input
            type="text"
            name="to"
            value={this.state.to}
            onChange={this.handleInput}
            className="form-control"
          />
          <button type="submit" className="btn btn-dark">
            Search
          </button>
        </form>
        <table className="table table-bordered ">
          <thead className="bg-dark text-white border border-2 m">
            <tr>
              <th>Number</th>
              <th>From</th>
              <th>To</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {this.state.result.map(flight => (
              <tr>
                <td>{flight.number}</td>
                <td>{flight.from}</td>
                <td>{flight.to}</td>
                <td>{flight.date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Search;
