import React, { Component } from "react";
import Register from "./components/Register";
export class App extends Component {
  state = {
    registeration: []
  };
  // registerFunt

  registerFunc = info => {
    this.state.registeration.push(info);
    this.forceUpdate();
  };
  registerFunc;
  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-12 bg-dark">
            <h1>Header</h1>
          </div>
        </div>
        <div className="row">
          <div className="col-lg-4 bg-info">
            <Register registerInfo={this.registerFunc} />
          </div>
          <div className="col-lg-8 bg-warning">
            <h1>Information</h1>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
