import React, { Component } from "react";
import "./App.css";
import Register from "./components/Register";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Header from "./components/Header";
import Home from "./components/Home";
import About from "./components/About";
import Logo from "./tomas.png";
import Visa from "./visa.png";
import Master from "./master.png";
import Search from "./components/Search";
export class App extends Component {
  state = {
    registeration: [
      {
        from: "tehran",
        to: "najaf",
        number: "23232"
      }
    ],
    search: []
  };
  // push new object to registeration

  pushNewObject = info => {
    this.state.registeration.push(info);
    this.forceUpdate();
    console.log(this.state);
  };
  //  delete
  deleteElement = event => {
    event.preventDefault();

    const registerationArray = this.state.registeration.filter(
      element => element.number !== event.target.value
    );

    this.setState({
      registeration: registerationArray
    });
  };
  // search
  searchForFlight = event => {
    event.preventDefault();
    const resultArray = this.state.registeration.indexOf(event.target.value);

    this.setState({
      search: resultArray
    });
    console.log(this.search);
  };

  render() {
    return (
      <Router>
        <div className="container">
          <div className="row">
            <div className="col-12 bg-secondary text-center p-0">
              <img src={Logo} alt="tomas" width="100px" />
              <Header />
            </div>
          </div>
          <div className="row">
            <div className="col-12 p-0 text-center">
              <Switch>
                <React.Fragment>
                  <Route path="/" exact component={Home} />
                  <Route path="/about" exact component={About} />
                </React.Fragment>
              </Switch>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-4 pt-2">
              <h3 className="text-center">Register</h3>
              <Register referPropsObject={this.pushNewObject} />
            </div>
            <div className="col-lg-8 text-center pt-2">
              <div>
                <Search searchForFlight={this.searchForFlight} />
              </div>
              <h3>Information</h3>
              <table className="table bg-white">
                <thead>
                  <tr>
                    <th>Number</th>
                    <th>From</th>
                    <th>To</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.registeration.map(register => (
                    <tr key={register.number}>
                      <td>{register.number}</td>
                      <td>{register.from}</td>
                      <td>{register.to}</td>
                      <td>{register.date}</td>
                      <td>
                        {
                          <button
                            type="button"
                            className="btn btn-danger"
                            value={register.number}
                            onClick={this.deleteElement}
                          >
                            x
                          </button>
                        }
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
          <div className="row">
            <div className="col-12 text-center">
              <span>* Currently supports all VISA and MasterCard cards</span>
              <img src={Visa} alt="visa" width="50px" />
              <img src={Master} alt="master" width="50px" />
            </div>
          </div>
          <div className="row">
            <div className="col-12 bg-secondary p-4 text-white text-center">
              <p>Copyright © 2019. Tomas Cook, All rights reserved.</p>
            </div>
          </div>
        </div>
      </Router>
    );
  }
}

export default App;
