import React, { Component } from "react";
import shortid from "shortid";
export class Search extends Component {
  state = {
    stopName: "", // I changed this
    origin: "", // I changed this
    id: "",
    result: []
  };
  //   input
  handleInput = event => {
    event.preventDefault();

    this.setState({
      id: shortid.generate(),
      [event.target.name]: event.target.value
    });
  };
  //   submit
  handleSubmit = event => {
    event.preventDefault();

    const result = this.props.searchObj.filter(
      flight =>
        flight.stopName === this.state.stopName &&
        flight.origin === this.state.origin
    );
    this.setState({
      result: result
    });
    this.forceUpdate();
    console.log(result);
    this.setState({
      stopName: "",
      origin: ""
    });
  };
  render() {
    return (
      <div>
        <form className="form-row" onSubmit={this.handleSubmit}>
          <div className="row">
            <div className="col">
              <input
                type="text"
                name="stopName" // I changed this
                value={this.state.stopName} // stopName is a property in api object
                onChange={this.handleInput}
                className="form-control"
                placeholder="From"
              />
            </div>
            <div className="col">
              <input
                type="text"
                name="origin" // I changed this
                value={this.state.origin} // origin is a property in api object
                onChange={this.handleInput}
                className="form-control"
                placeholder="To"
              />
            </div>
            <div className="col">
              <button type="submit" className="btn btn-dark">
                Search
              </button>
            </div>
          </div>
        </form>
        <table className="table table-bordered ">
          <thead className="bg-dark text-white">
            {/*  I changed this */}
            <tr>
              <th>Name</th>
              <th>Type</th>
              <th>StopName</th>
              <th>Date</th>
              <th>Origin</th>
              <th>Track</th>
            </tr>
          </thead>
          <tbody className="bg-light">
            {/*  I changed this */}
            {this.state.result.map(trip => (
              <tr key={trip.detailsId}>
                <td>{trip.name}</td>
                <td>{trip.type}</td>
                <td>{trip.stopName}</td>
                <td>{trip.dateTime}</td>
                <td>{trip.origin}</td>
                <td>{trip.track}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Search;
