import React, { Component } from "react";
import Register from "./components/Register";

// import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
// import Header from "./components/Header";
export class App extends Component {
  state = {
    registeration: []
  };
  // registerFunt

  registerFunc = info => {
    this.state.registeration.push(info);
    this.forceUpdate();
    console.log(this.state);
  };
  //  delete
  deleteElement = event => {
    event.preventDefault();

    const registerationArray = this.state.registeration.filter(
      element => element.id !== event.target.value
    );

    this.setState({
      registeration: registerationArray
    });
  };

  /* 
(event)=>(



*/
  render() {
    return (
      // <Router>
      <div className="container-fluid ">
        <div className="row">
          <div className="col-12 bg-dark text-center text-white p-3">
            {/* <Route path="/about" exact component={Header} /> */}
            <h1>Tomas Cook</h1>
          </div>
        </div>
        <div className="row">
          <div className="col-lg-4 bg-info">
            <h3 className="text-center">Register</h3>
            <Register registerInfo={this.registerFunc} />
          </div>
          <div className="col-lg-8 bg-warning text-center">
            <h3>Information</h3>
            <table className="table bg-white">
              <thead>
                <tr>
                  <th>#</th>
                  <th>From</th>
                  <th>To</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {this.state.registeration.map(register => (
                  <tr key={register.id}>
                    <td>{register.id}</td>
                    <td>{register.from}</td>
                    <td>{register.to}</td>
                    <td>{register.date}</td>
                    <td>
                      {
                        <button
                          type="button"
                          className="btn btn-danger"
                          value={register.id}
                          onClick={this.deleteElement}
                        >
                          x
                        </button>
                      }
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      // </Router>
    );
  }
}

export default App;
