import React, { Component } from "react";
import Register from "./components/Register";
export class App extends Component {
  state = {
    registeration: [
      {
        from: "ed",
        to: "re",
        date: "02-03-2019"
      }
    ]
  };
  // registerFunt

  registerFunc = info => {
    this.state.registeration.push(info);
    this.forceUpdate();
    console.log(this.state);
  };

  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-12 bg-dark">
            <h1>Header</h1>
          </div>
        </div>
        <div className="row">
          <div className="col-lg-4 bg-info">
            <h3>Register</h3>
            <Register registerInfo={this.registerFunc} />
          </div>
          <div className="col-lg-8 bg-warning">
            <h3>Information</h3>
            <table>
              <thead>
                <tr>
                  <th>#</th>
                  <th>From</th>
                  <th>To</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {this.state.registeration.map(register => (
                  <tr key={register.id}>
                    <td>{register.id}</td>
                    <td>{register.from}</td>
                    <td>{register.to}</td>
                    <td>{register.date}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
